/*reset*/
* {
  box-sizing: border-box;
}
*::after,
*::before {
  box-sizing: border-box;
}
* {
  margin: 0;
  padding: 0;
}
ol,
ul {
  list-style: none;
}
a {
  color: inherit;
  text-decoration: none;
}
img {
  max-width: 100%;
  max-height: 100%;
}
.iconpark-icon {
  width: 1em;
  height: 1em;
}
/*style*/
body {
  background-color: #eee;
}
.globalHeader {
  margin: 80px 32px;
}
.searchFrom {
  display: flex;
  justify-content: space-between;
}
@media (min-width: 500px) {
  .searchFrom {
    max-width: 600px;
    height: 140;
    margin-left: auto;
    margin-right: auto;
    margin-top: 200px;
  }
}
.searchFrom > input {
  height: 40px;
  width: 100%;
  margin-right: 10px;
  padding: 0 20px;
  border: 1px solid #ddd;
  border-radius: 50px;
  font-size: 15px;
}
.searchFrom > button {
  white-space: nowrap;
  background-color: #31a6bf;
  border: none;
  font-size: 16px;
  border-radius: 50px;
  padding: 0 28px;
  color: white;
}
.globalMain {
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
}
.siteList > li {
  margin-bottom: 20px;
  margin-right: 45px;
}
.siteList {
  display: flex;
  flex-wrap: wrap;
}
@media (max-width: 500px) {
  .siteList > li {
    margin-right: 0px;
  }
  .siteList {
    margin: 32px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
  }
}
.siteList .site {
  display: flex;
  width: 155px;
  padding: 20px;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: white;
  position: relative;
  border-radius: 5px;
  cursor: pointer;
}
.siteList .site:hover {
  -webkit-box-shadow: 0px 3px 3px #c8c8c8;
  -moz-box-shadow: 0px 3px 3px #c8c8c8;
  box-shadow: 0px 3px 3px #c8c8c8;
}
.siteList .site > .logo {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 64px;
}
.siteList .site > .link {
  margin-top: 4px;
  font-size: 14px;
}
.siteList .site > .close {
  position: absolute;
  right: 8px;
  top: 8px;
  display: none;
}
.siteList .site:hover > .close {
  display: block;
}

.siteList .addButton {
  display: flex;
  width: 155px;
  padding: 20px;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin-bottom: 10px;
  background-color: white;
  border-radius: 5px;
}
.siteList .addButton:hover {
  -webkit-box-shadow: 0px 3px 3px #c8c8c8;
  -moz-box-shadow: 0px 3px 3px #c8c8c8;
  box-shadow: 0px 3px 3px #c8c8c8;
}

.siteList .addButton > .text {
  margin-top: 4px;
  font-size: 14px;
}
.siteList .addButton > .add {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 64px;
}
